const fs = require("fs");
const xValue = require("xValue.json");

const a = 5;
const c = 0;
const m = Math.pow(2, 32);

for (var i = 0; i < 10; i++) {
    xValue.research[i] = 0;
}

for (var i = 0; i < 1000; i++) {
    xValue.remainder = (a * xValue.remainder + c) % m;
    var rng = Math.round(JSON.stringify(xValue.remainder).slice(JSON.stringify(xValue.remainder).length - 3, JSON.stringify(xValue.remainder).length - 2));
    xValue.research[rng]++;
    console.log(rng);
}

fs.writeFile("xValue.json", JSON.stringify(xValue), "utf8", err => {
    if(err) throw err;
});